import React from 'react'


function AddQuestion() {
    const validatingAnswers=(key,value)=>{
        switch (key){
            case "questionDetail":
                if(value===""){
                    console.log(value)
                    document.getElementById("validatingQuestionDetails").classList.remove('d-none')
                    document.getElementById("validatingQuestionDetails").classList.add('d-block')
                    document.getElementById("validatingQuestionDetails").innerHTML="*Please enter a question details";
                }
                break;
            case "optionA":
                if(value===""){
                    document.getElementById("validatingOptionA").classList.remove('d-none')
                    document.getElementById("validatingOptionA").classList.add('d-block')
                    document.getElementById("validatingOptionA").innerHTML="*Please enter a value";
                }
                break;
            case "optionB":
                if(value===""){
                    document.getElementById("validatingOptionB").classList.remove('d-none')
                    document.getElementById("validatingOptionB").classList.add('d-block')
                    document.getElementById("validatingOptionB").innerHTML="*Please enter a value";
                }
    
                break;
            case "optionC":
                if(value===""){
                    document.getElementById("validatingOptionC").classList.remove('d-none')
                    document.getElementById("validatingOptionC").classList.add('d-block')
                    document.getElementById("validatingOptionC").innerHTML="*Please enter a value";
                }
                break;
            case "optionD":
                if(value===""){
                    document.getElementById("validatingOptionD").classList.remove('d-none')
                    document.getElementById("validatingOptionD").classList.add('d-block')
                    document.getElementById("validatingOptionD").innerHTML="*Please enter a value";
                }
                break;
            case "optionE":
                if(value===""){
                    document.getElementById("validatingOptionE").classList.remove('d-none')
                    document.getElementById("validatingOptionE").classList.add('d-block')
                    document.getElementById("validatingOptionE").innerHTML="*Please enter a value";
                }
                break;
            case "answer":
                if(value===""){
                    document.getElementById("validatingAnswer").classList.remove('d-none')
                    document.getElementById("validatingAnswer").classList.add('d-block')
                    document.getElementById("validatingAnswer").innerHTML="*Please enter the answer";
                }
                break;
            case "numberofanswer":
                if(value===""){
                    document.getElementById("validatingNumberofanswer").classList.remove('d-none')
                    document.getElementById("validatingNumberofanswer").classList.add('d-block')
                    document.getElementById("validatingNumberofanswer").innerHTML="*Please enter a number of answers"
                }
                break;
            case "answervalue":
                if(value===""){
                    document.getElementById("validatingAnswervalue").classList.remove('d-none')
                    document.getElementById("validatingAnswervalue").classList.add('d-block')
                    document.getElementById("validatingAnswervalue").innerHTML="*please enter a answervalue";
                }
                break;
            case "topicid":
                if(value===""){
                    document.getElementById("validatingTopicid").classList.remove('d-none')
                    document.getElementById("validatingTopicid").classList.add('d-block')
                    document.getElementById("validatingTopicid").innerHTML="*Please enter the topicid"
                }
                break;
            case "negativemarkvalue":
                if(value===""){
                    document.getElementById("validatingNegativemarkvalue").classList.remove('d-none')
                    document.getElementById("validatingNegativemarkvalue").classList.add('d-block')
                    document.getElementById("validatingNegativemarkvalue").innerHTML="*Please enter the markvalue"
                }
