import React, { useEffect, useState } from "react";
import useStateRef from "react-usestateref";
import UpdateQuestion from './UpdateQuestion'
const ViewQuestion = () => {
   
  const [list, setListQuestions, refListQuestions] = useStateRef();

  useEffect(() => {
    fetchCall();
  },[]);

  function fetchCall() {
    fetch("https://localhost:8443/onlineexamapplication/control/viewQuestions")
      .then((res) => res.json())
      .then((data) => {
        var questionList = data.questionList;
       
        setListQuestions(questionList);
       
      });
  }

     
    //  function for delete button
    const deleteQuestion=(id)=>{
      var questionId=id;
      
         fetch(`https://localhost:8443/onlineexamapplication/control/delete?questionId=${questionId}`)
          
        
    }

  return (
    <>

    {/*  */}
  
    <div className="container">
    <table className='table w-75 offset-2 border border- my-5'>
     <thead className='table-light  my-5'>
        <tr>
            <th >QuestionId</th>
            <th>QuestionDetail</th>
            <th>TopicId</th>
            <th></th>
            <th></th>
            
        </tr>
     </thead>
     <tbody>
        {list && list.map((listData)=>{
      return(<>
       <tr>
            <td>{listData.questionId}</td>
            <td>Hii</td>
            <td>Welcome</td>
            <td>
                <button className="btn btn-primary offset-md-2">Edit</button>
                <button className="btn btn-danger offset-md-1">Delete</button>
            </td>
           
        </tr>
      </>)
        })}
       
     </tbody>
    </table>
    </div>
    {/*  */}
  
     <table class="table  table-hover">
  {/* <thead>
    <tr>
        <th>QuestionId</th>
       <th scope="col " style={{'float':'right'}}>QuestionDetail</th> 
    </tr>
  </thead> */}
  <tbody>
    {list && list.map((l)=>{
        return(<>
         <tr>
        <th>
         {l.questionId}
        </th>
        <th><a href={`http://localhost:3000/update-question?questionId=${l.questionId}&&topicId=${l.topicId}`} ><button className="btn btn-warning" style={{'float':'right'}}>Update</button></a></th>

        <th><button className="btn btn-danger" onClick={()=>deleteQuestion(l.questionId)} style={{'float':'right'}}>Delete</button></th>
    </tr>
        </>)
    })}
   
  </tbody>
     
</table>
{/* {updateState ? <UpdateQuestion updateQuestions={updateId} /> :""} */}
    </>
  );
};

export default ViewQuestion;
