import React, { useState,useEffect } from "react";
import logo from "./onlinelogo.png";
import Modal from "./Modal";

const Header = () => {
  const [flag,setFlag]=useState(true);
  const [name,setName]=useState("");
  async function ontrigger(){
    console.log("header use effect is fired")

const response= await fetch("https://localhost:8443/onlineexamapplication/control/userLogin")

const text=await response.json();

setName(text._UlastName_)

setFlag(text.isUserlogin)
console.log(text._UlastName_  )

  }

  
  useEffect(()=>{
    ontrigger()
  },[])
  return (
    <div>
      <Modal />
      
      <div className="container-fluid ">
        <div className="row">
          <nav className="navbar navbar-expand-sm bg-secondary navbar-dark custom-navbar">
            <div className="col-4 navbar-brand">
              <img src={logo} alt="online-exam-logo" className="custom-logo rounded-circle" />
              
            </div>
            <div className="col-4 text-center">
              <h3>Online Exam</h3>
            </div>
            <div className="col-4">
              <center>{flag===false?
                <button
                  className="btn btn-light btn-lg "
                  data-bs-toggle="modal"
                  data-bs-target="#enroll"
                >
                  Login
                </button>:<>welcome {name}
                <button className="btn btn-light ">Logout</button>
                </>}
              </center>
            </div>
          </nav>
        </div>
      </div>
    </div>
  );
};

export default Header;
